import React, { useEffect, useState } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";

const Carte = () => {
  const [carte, setCarte] = useState([]);
  const [selectedCard, setSelectedCard] = useState(null);
  const [isImageEnlarged, setIsImageEnlarged] = useState(null); // Gérer la carte sélectionnée

  useEffect(() => {
    axios
      .get("http://localhost:3001/carte")
      .then((response) => {
        setCarte(response.data);
      })
      .catch((error) => {
        console.error("Erreur lors de la récupération des cartes", error);
      });
  }, []);

  // Fonction pour gérer le clic sur une carte
  const handleCardClick = (id_carte) => {
    setSelectedCard(selectedCard === id_carte ? null : id_carte); // Affiche/cache la carte cliquée
    setIsImageEnlarged(isImageEnlarged === id_carte ? null : id_carte);
  };

  return (
    <div>
      <Navigation />
      <div className="cartes">
        <h1>Liste des Cartes</h1>
        {carte.length > 0 ? (
          <ul>
            {carte.map((carte) => (
              <li key={carte.id_carte} style={{ marginBottom: "20px" }}>
                <h2>{carte.Nom}</h2>

                {carte.image_url && (
                  <img
                    src={`http://localhost:3001${carte.image_url}`}
                    alt={carte.nom}
                    className={`clickable-image ${
                      isImageEnlarged === carte.id_carte ? "enlarged" : ""
                    }`}
                    onClick={() => handleCardClick(carte.id_carte)} // Clic pour agrandir/réduire l'imag
                  />
                )}

                {/* Affichage conditionnel du texte basé sur le clic */}
                {selectedCard === carte.id_carte && (
                  <div className="carte-details">
                    <p className="type">Type: {carte.type}</p>
                    <p className="cout">Coût: {carte.cout}</p>
                    <p className="puissance">Puissance: {carte.puissance} </p>
                    <p className="tag">Tag: {carte.tag} </p>
                    <p className="rarete">Rareté: {carte.rarete} </p>
                    <p className="extension">Extension: {carte.extension} </p>
                    <p className="effet">Effet: {carte.effet} </p>
                  </div>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p>Aucune carte trouvée</p>
        )}
      </div>
    </div>
  );
};

export default Carte;
