import React, { useState } from "react";
import AjoutCarteDeck from "../components/AjoutCarteDeck";
import axios from "axios";

const Deck = () => {
  const [deck, setDeck] = useState([]); // Stocker les cartes du deck

  // Fonction pour ajouter une carte au deck
  const ajouterCarteAuDeck = (carte) => {
    setDeck([...deck, carte]); // Ajouter la carte localement dans le deck

    // Envoyer la carte au back-end pour l'enregistrer dans la base de données
    axios
      .post("http://localhost:3001/deck", { carteId: carte.id })
      .then((response) => console.log("Carte ajoutée au deck", response))
      .catch((error) =>
        console.error("Erreur lors de l'ajout de la carte au deck", error)
      );
  };

  return (
    <div className="builder">
      <h1>Deckbuilder</h1>

      {/* Composant pour afficher les cartes disponibles et les ajouter au deck */}
      <AjoutCarteDeck ajouterCarteAuDeck={ajouterCarteAuDeck} />

      {/* Affichage des cartes du deck */}
      <div className="deck">
        <h2>Votre Deck</h2>
        <ul>
          {deck.map((carte, index) => (
            <li key={index}>
              <p>{carte.nom}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Deck;
