import React, { useState } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";

const Inscription = () => {
  // Utilisation de useState pour gérer les données du formulaire
  const [formData, setFormData] = useState({
    username: "",
    firstname: "",
    pseudo: "",
    charactere: "",
    area: "",
    store: "",
    password: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:3001/register",
        formData
      );
      if (response.status === 200) {
        // Les données ont été enregistrées avec succès
        console.log("Form submitted and data saved:", response.data);
        localStorage.setItem("usersPseudo", formData.pseudo);
      } else {
        console.error("Failed to save data");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <div>
      <Navigation />
      <h1>Inscription</h1>
      <form onSubmit={handleSubmit} className="Inscription">
        <input
          type="text"
          name="username"
          value={formData.username || ""}
          placeholder="Entrez votre nom"
          onChange={handleChange}
        />

        <input
          type="text"
          name="firstname"
          value={formData.firstname || ""}
          placeholder="Entrez votre prenom"
          onChange={handleChange}
        />
        <input
          type="text"
          name="pseudo"
          value={formData.pseudo || ""}
          placeholder="Entrez votre pseudo"
          onChange={handleChange}
        />
        <input
          type="text"
          name="charactere"
          value={formData.charactere || ""}
          placeholder="Entrez votre personnage preféré"
          onChange={handleChange}
        />
        <input
          type="text"
          name="area"
          value={formData.area || ""}
          placeholder="Entrez votre région"
          onChange={handleChange}
        />
        <input
          type="text"
          name="store"
          value={formData.store || ""}
          placeholder="Entrez votre boutique habituel"
          onChange={handleChange}
        />
        <input
          type="text"
          name="password"
          value={formData.password || ""}
          placeholder="Entrez votre mot de passe"
          onChange={handleChange}
        />

        <input type="submit" value="valider" />
      </form>
    </div>
  );
};

export default Inscription;
