import React, { useState, useEffect } from "react";
import axios from "axios";

const Profil = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Supposons que le pseudo de l'utilisateur soit stocké dans le localStorage après la connexion ou l'inscription
    const pseudo = localStorage.getItem("userPseudo");

    // Faire une requête pour récupérer les informations utilisateur
    axios
      .get(`http://localhost:3001/user/${pseudo}`)
      .then((response) => {
        setUserData(response.data);
      })
      .catch((error) => {
        console.error(
          "Erreur lors de la récupération des données de l'utilisateur:",
          error
        );
      });
  }, []);

  // Afficher les informations de l'utilisateur
  return (
    <div>
      <h1>Profil</h1>
      {userData ? (
        <div>
          <p>Nom: {userData.username}</p>
          <p>Prénom: {userData.firstname}</p>
          <p>Pseudo: {userData.pseudo}</p>
          <p>Personnage Préféré: {userData.character}</p>
          <p>Région: {userData.area}</p>
          <p>Boutique: {userData.store}</p>
        </div>
      ) : (
        <p>Chargement des données...</p>
      )}
    </div>
  );
};

export default Profil;
