import React, { useState } from "react";
import Navigation from "../components/Navigation";

const Inscription = () => {
  const [formData, setFormData] = useState({
    username: "",
    firstname: "",
    pseudo: "",
    character: "",
    area: "",
    store: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: <e className="target value"></e>,
    });
  };

  return (
    <div>
      <Navigation />
      <h1>Inscription</h1>
      <form action="" className="Inscription">
        <input type="text" placeholder="Entrez votre nom" />
        <input type="text" placeholder="Entrez votre prenom" />
        <input type="text" placeholder="Entrez votre pseudo" />
        <input type="text" placeholder="Entrez votre personnage preféré" />
        <input type="text" placeholder="Entrez votre région" />
        <input type="text" placeholder="Entrez votre boutique habituel" />
        <input type="submit" value="valider" />
      </form>
    </div>
  );
};

export default Inscription;
