import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import LogoutButton from "./Deconnexion";

const Navigation = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <div className="navigation">
      <ul>
        {isLoggedIn && (
          <>
            <NavLink
              to="/profil"
              className={(nav) => (nav.isActive ? "nav-active" : "")}
            >
              <li className="Profil ">Profil</li>
            </NavLink>
          </>
        )}
        <NavLink to="/" className={(nav) => (nav.isActive ? "nav-active" : "")}>
          <li>Accueil</li>
        </NavLink>

        <NavLink
          to="/builder"
          className={(nav) => (nav.isActive ? "nav-active" : "")}
        >
          <li>Builder</li>
        </NavLink>
        <NavLink
          to="/cartes"
          className={(nav) => (nav.isActive ? "nav-active" : "")}
        >
          <li>Cartes</li>
        </NavLink>
        <NavLink
          to="/deck"
          className={(nav) => (nav.isActive ? "nav-active" : "")}
        >
          <li>Deck</li>
        </NavLink>

        <NavLink
          to="/tournois"
          className={(nav) => (nav.isActive ? "nav-active" : "")}
        >
          <li>Tournois</li>
        </NavLink>
        {!isLoggedIn && (
          <>
            <NavLink
              to="/inscription"
              className={(nav) => (nav.isActive ? "nav-active" : "")}
            >
              <li>Inscription</li>
            </NavLink>{" "}
          </>
        )}

        <NavLink
          to="/ajout"
          className={(nav) => (nav.isActive ? "nav-active" : "")}
        >
          <li>Ajout carte</li>
        </NavLink>

        <NavLink
          to="/connexion"
          className={(nav) => (nav.isActive ? "nav-active" : "")}
        >
          <li>Connexion</li>
        </NavLink>

        <li>
          <LogoutButton />
        </li>

        <li className="logo"></li>
      </ul>
    </div>
  );
};

export default Navigation;
