import React, { useEffect, useState } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";

const Cartes = () => {
  const [cartes, setCartes] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/carte")

      .then((response) => {
        setCartes(response.data);
      })
      .catch((error) => {
        console.error("Erreur lors de la récuperation des cartes", error);
      });
  }, []);

  return (
    <div>
      <Navigation />
      <div className="cartes">
        <h1>Liste des Cartes</h1>
        {cartes.length > 0 ? (
          <ul>
            {cartes.map((carte) => (
              <li key={cartes.id}>
                <h2>{cartes.nom}</h2>
                <p>Coût: {cartes.cout}</p>
                <p>Puissance: {cartes.puissance} </p>
                <p>Effet: {cartes.effet} </p>
              </li>
            ))}
          </ul>
        ) : (
          <p> Aucune carte trouvée</p>
        )}
      </div>
    </div>
  );
};

export default Cartes;
