import React, { useState, useEffect } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";
import { jwtDecode } from "jwt-decode";

const Builder = ({ builder, enregistrerDeck }) => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      // Si aucun token, redirige vers la page de connexion ou traite l'absence de token
      console.error(
        "Aucun token trouvé, redirection vers la page de connexion"
      );
      localStorage.removeItem("token");
      window.location.href = "/connexion";
      return;
    }

    try {
      const decodedToken = jwtDecode(token);
      const pseudo = decodedToken.pseudo;

      // Exemple de vérification du token
      axios
        .get(`http://localhost:3001/users/${pseudo}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setUserData(response.data);
        })
        .catch((error) => {
          console.error("Erreur lors de la récupération du profil:", error);
          localStorage.removeItem("token");
          window.location.href = "/connexion";
        });
    } catch (error) {
      console.error("Erreur lors du décodage du token:", error);
      localStorage.removeItem("token");
      window.location.href = "/connexion";
    }
  }, []); // Dépendances vides, s'exécute une seule fois au montage du composant

  return (
    <div>
      <Navigation />
      <div className="builder">
        <h1>Builder</h1>
        <ul>
          {DeckBuilder.map((carte, index) => (
            <li key={index}>{carte.nom} </li>
          ))}
        </ul>
        <button onClick={enregistrerDeck}>Enregistrer Deck</button>
      </div>
    </div>
  );
};

export default Builder;
