import React, { useEffect, useState } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";

const Carte = () => {
  // État pour suivre si l'image est agrandie
  const [isExpanded, setIsExpanded] = useState(false);

  // Gérer le clic sur l'image
  const handleImageClick = () => {
    setIsExpanded(!isExpanded); // Alterne entre agrandi et réduit
  };
  const [carte, setCarte] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/carte")

      .then((response) => {
        setCarte(response.data);
      })
      .catch((error) => {
        console.error("Erreur lors de la récuperation des cartes", error);
      });
  }, []);

  return (
    <div>
      <Navigation />
      <div className={`image-container ${isExpanded ? "expanded" : ""}`}>
        <h1>Liste des Cartes</h1>
        {carte.length > 0 ? (
          <ul>
            {carte.map((carte) => (
              <li key={carte.id_carte}>
                <h2>{carte.Nom}</h2>
                {carte.image_url && (
                  <img
                    src={`http://localhost:3001${carte.image_url}`}
                    alt={carte.nom}
                    style={{ width: "200px", height: "auto" }}
                    className="clickable-image"
                    onClick={handleImageClick}
                  />
                )}
                <p className="type">Type: {carte.type}</p>
                <p className="cout">Coût: {carte.cout}</p>
                <p className="puissance">Puissance: {carte.puissance} </p>
                <p className="tag">Tag: {carte.tag} </p>
                <p className="rarete">rareté: {carte.rarete} </p>
                <p className="extension">extension: {carte.extension} </p>
                <p className="effet">Effet: {carte.effet} </p>
              </li>
            ))}
          </ul>
        ) : (
          <p> Aucune carte trouvée</p>
        )}
        <div className="info-box">
          <h2>Informations</h2>
          <p>Ligne 1: Détail important</p>
          <p>Ligne 2: Autre information</p>
          <p>Ligne 3: Détail supplémentaire</p>
          <p>Ligne 4: Dernière information</p>
        </div>
      </div>
    </div>
  );
};

export default Carte;
