import React, { useState, useEffect } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";
import { jwtDecode } from "jwt-decode";

const Profil = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      navigate("/connexion", {
        // Redirige vers la page de connexion si non authentifié
        state: { from: location.pathname }, // Enregistre l'URL d'origine
      });
      return; // Ajout d'un return pour éviter d'exécuter le code suivant si non authentifié
    }

    const decodedToken = jwtDecode(token);
    const pseudo = decodedToken.pseudo;

    // Exemple de vérification du token
    axios
      .get(`http://localhost:3001/users/${pseudo}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setUserData(response.data);
      })
      .catch((error) => {
        console.error("Erreur lors de la récupération du profil:", error);
        localStorage.removeItem("token");
      });
  });

  // Afficher les informations de l'utilisateur
  return (
    <div>
      <Navigation />
      <h1>Profil</h1>
      {userData ? (
        <div>
          <p>Nom: {userData.username}</p>
          <p>Prénom: {userData.firstname}</p>
          <p>Pseudo: {userData.pseudo}</p>
          <p>Personnage Préféré: {userData.personnage}</p>
          <p>Région: {userData.area}</p>
          <p>Boutique: {userData.store}</p>
        </div>
      ) : (
        <p>Chargement des données...</p>
      )}
    </div>
  );
};

export default Profil;
