import React, { useState, useEffect } from "react";
import axios from "axios";

const AjoutCarteDeck = ({ ajouterCarteAuDeck }) => {
  const [cartes, setCartes] = useState([]); // Stocker les cartes disponibles

  // Récupérer les cartes du back-end lors du chargement du composant
  useEffect(() => {
    axios
      .get("http://localhost:3001/carte")
      .then((response) => setCartes(response.data))
      .catch((error) =>
        console.error("Erreur lors de la récupération des cartes", error)
      );
  }, []);

  return (
    <div className="liste-cartes">
      <h2>Liste des Cartes</h2>
      <ul>
        {cartes.map((carte) => (
          <li key={carte.id}>
            <p>{carte.nom}</p>
            <button onClick={() => ajouterCarteAuDeck(carte)}>
              Ajouter au deck
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AjoutCarteDeck;
